from(bucket: "home/autogen")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r._measurement == "solar" and r._field == "AC_Watt")
  |> aggregateWindow(every: v.windowPeriod, fn: mean)
  |> map(fn: (r) => ({
      r with
      _value: -r._value
    })
  )

